// Function to get all todos
export const getTodos = async () => {
    const response = await axios.post(
        `${'https://data.mongodb-api.com/app/data-msbfigv/endpoint/data/v1/action'}/find`,
        {
            dataSource: 'ClusterTODO',
            database: 'TODO-Database',
            collection: 'TODO',
            filter: {}
        },
        {
            headers: {
                'Content-Type': 'application/json',
                'api-key': 's3cdYXbafzx3fsM5JDsAqnW49vZbgF7ZBKGS6rG042XdPzHIV1atJgDZYYZ6gFih'
            }
        }
    );
    return response.data.documents;
};

// Function to create a new todo
export const createTodo = async (todo) => {
    const response = await axios.post(
        `${'https://data.mongodb-api.com/app/data-msbfigv/endpoint/data/v1/action'}/insertOne`,
        {
            dataSource: 'Cluster0',
            database: 'todo-app',
            collection: 'todos',
            document: todo
        },
        {
            headers: {
                'Content-Type': 'application/json',
                'api-key': 's3cdYXbafzx3fsM5JDsAqnW49vZbgF7ZBKGS6rG042XdPzHIV1atJgDZYYZ6gFih'
            }
        }
    );
    return response.data;
};

// Function to update a todo
export const updateTodo = async (id, updatedTodo) => {
    const response = await axios.post(
        `${'https://data.mongodb-api.com/app/data-msbfigv/endpoint/data/v1/action'}/updateOne`,
        {
            dataSource: 'Cluster0',
            database: 'todo-app',
            collection: 'todos',
            filter: { _id: { $oid: id } },
            update: {
                $set: updatedTodo
            }
        },
        {
            headers: {
                'Content-Type': 'application/json',
                'api-key': 's3cdYXbafzx3fsM5JDsAqnW49vZbgF7ZBKGS6rG042XdPzHIV1atJgDZYYZ6gFih'
