import axios from 'axios';

const API_URL = 'http://localhost:5000/todos';

export const getTodos = async () => {
    const response = await axios.get("https://ap-south-1.aws.data.mongodb-api.com/app/data-msbfigv/endpoint/data/v1");
    return response.data;
};

export const createTodo = async (todo) => {
    const response = await axios.post("https://ap-south-1.aws.data.mongodb-api.com/app/data-msbfigv/endpoint/data/v1", todo);
    return response.data;
};

export const updateTodo = async (id, updatedTodo) => {
    const response = await axios.put(`${"https://ap-south-1.aws.data.mongodb-api.com/app/data-msbfigv/endpoint/data/v1"}/${id}`, updatedTodo);
    return response.data;
};

export const deleteTodo = async (id) => {
    const response = await axios.delete(`${"https://ap-south-1.aws.data.mongodb-api.com/app/data-msbfigv/endpoint/data/v1"}/${id}`);
    return response.data;
};
