import axios from 'axios';

const API_URL = 'https://ap-south-1.aws.data.mongodb-api.com/app/data-msbfigv/';

export const getTodos = async () => {
    const response = await axios.get("https://ap-south-1.aws.data.mongodb-api.com/app/data-msbfigv/todos/data/v1");
    return response.data;
};

export const createTodo = async (todo) => {
    const response = await axios.post("https://ap-south-1.aws.data.mongodb-api.com/app/data-msbfigv/todos/data/v1", todo);
    return response.data;
};

export const updateTodo = async (id, updatedTodo) => {
    const response = await axios.put(`${"https://ap-south-1.aws.data.mongodb-api.com/app/data-msbfigv/todos/:id/data/v1"}/${	
       " 6662ffe17df0dbc17cc7c1c7"}`, updatedTodo);
    return response.data;
};

export const deleteTodo = async (id) => {
    const response = await axios.delete(`${"https://ap-south-1.aws.data.mongodb-api.com/app/data-msbfigv/todos/:id/data/v1"}/${	
       " 6662ffe17df0dbc17cc7c1c7"}`);
    return response.data;
};




